mapper.select:
WITH recursive cte (
) AS ( SELECT
FROM SYS_MENU WHERE ID IN
#{item,jdbcType=VARCHAR}
UNION ALL SELECT SYS_MENU.ID, SYS_MENU.PARENT_ID, SYS_MENU.NAME, SYS_MENU.URL, SYS_MENU.ICON, SYS_MENU.TYPE, SYS_MENU.REMARK, SYS_MENU.`ORDER`, SYS_MENU.STATUS, SYS_MENU.UPDATE_SYS_USER_ID, SYS_MENU.CREATE_SYS_USER_ID, SYS_MENU.UPDATE_DATE_TIME, SYS_MENU.CREATE_DATE_TIME FROM SYS_MENU INNER JOIN cte ON cte.ID = SYS_MENU.PARENT_ID ) SELECT
FROM cte;

