mapper.select:
WITH recursive cte (
) AS ( SELECT
FROM SYS_ORGANIZATION WHERE ID IN
#{item,jdbcType=VARCHAR}
UNION ALL SELECT SYS_ORGANIZATION.ID, SYS_ORGANIZATION.PARENT_ID, SYS_ORGANIZATION.NAME, SYS_ORGANIZATION.FULL_NAME, SYS_ORGANIZATION.TYPE, SYS_ORGANIZATION.REMARK, SYS_ORGANIZATION.STATUS, SYS_ORGANIZATION.UPDATE_SYS_USER_ID, SYS_ORGANIZATION.CREATE_SYS_USER_ID, SYS_ORGANIZATION.UPDATE_DATE_TIME, SYS_ORGANIZATION.CREATE_DATE_TIME FROM SYS_ORGANIZATION INNER JOIN cte ON cte.ID = SYS_ORGANIZATION.PARENT_ID ) SELECT
FROM cte;

